mapper.select:
SELECT notice_id, notice_title, notice_contents, created_at
  FROM tbl_notice
 WHERE notice_id = #{noticeId}

SELECT attach_id, notice_id, file_path, original_filename, filesystem_name, download_count
  FROM tbl_attach
 WHERE notice_id = #{noticeId}

SELECT attach_id, notice_id, file_path, original_filename, filesystem_name, download_count
  FROM tbl_attach
 WHERE attach_id = #{attachId}

mapper.update:
UPDATE tbl_attach
   SET download_count = download_count + 1
 WHERE attach_id = #{attach_id}

mapper.select:
SELECT blog_id, title, contents, user_email, hit, modify_dt, create_at
  FROM tbl_blog
 ORDER BY id ${sort} 
 LIMIT #{offset}, #{display}
